select CAST(inicio_lanzamiento as DATE) as fecha,sum(mail_enviados) as enviados,sum(mail_rebotados) as rebotados,sum(mail_open_rate) as abiertos from mai_accioneanalisisenviosecs  WHERE inicio_lanzamiento >= DATEADD([month], DATEDIFF([month], '19000101', GETDATE()) - 3, '19000101') AND inicio_lanzamiento < DATEADD([month], DATEDIFF([month], '19000101', GETDATE()), '19000101') group by CAST(inicio_lanzamiento as DATE) order by CAST(inicio_lanzamiento as DATE)
